import React from 'react';
import { Route, Redirect } from "react-router-dom";

const ProtectedRoute = ({ loggedIn, children, ...props }) => {
  return(
    <>
      loggedIn ? (
        <Route {...props}>
          {children}
        </Route>
      )
      :
      <Redirect to="./sign-in" />
    </>
  )
};

export default ProtectedRoute;

