import React from 'react';
import headerLogo from '../images/headerLogo.svg';
import { Link, withRouter } from 'react-router-dom'

class Header extends React.Component {
  constructor(props) {
    super(props)
  }

  switchLink = () => {
    let link = { location: '', title: '' }
    const currentLocation = this.props.history.location.pathname
    if (currentLocation === "/") {
      return link = { location: '/sign-in', title: 'Выйти' }
    } else if (currentLocation === '/sign-in') {
      return link = { location: '/sign-up', title: 'Зарегистрироваться' }
    } else if (currentLocation === '/sign-up') {
      return link = { location: '/sign-in', title: 'Войти' }
    }
  }

  render() {
    return (
      <header className="header">
        <img className="header__logo" src={headerLogo} alt="Логотип сайта" />
        <p className="site-entrance__redirect-link">{this.props.email}</p>
        <Link className="site-entrance__redirect-link" onClicto={this.switchLink().location}>{this.switchLink().title}</Link>
      </header>
    );
  }
}

export default withRouter(Header)
