import React from 'react'
import { CurrentUserContext } from '../contexts/CurrentUserContext'
import { api } from '../utils/Api.js'

class Card extends React.Component {

  static contextType = CurrentUserContext

  constructor(props) {
    super(props)
  }

  isOwn = () => {
    return this.props.card.owner._id === this.context._id
  }

  isLiked = () => {
    return this.props.card.likes.some(i => i._id === this.context._id)
  }

  handleCardLike = () => {
    this.isLiked() ? 
    api.removeLike() :
    api.setLike()
  }

  handleClick = () => {
    this.props.onCardClick(this.props.card)
    console.log(this.isLiked())
  }

  render() {
    return(
      <li className="element">
        <img className="element__image" src={this.props.card.link} alt={this.props.card.name} onClick={this.handleClick}/>
        <button className={`element__delete-button ${this.isOwn() ? 'element__delete-button_visible' : ''}`} type="button"></button>
        <div className="element__bottom-bar">
          <h2 className="element__title">{this.props.card.name}</h2>
          <button className={`element__like-button ${this.isLiked() ? 'element__like-button_active' : ''}`} type="button" onCardLike={this.handleCardLike}></button>
          <span className="element__like-counter">{this.props.card.likes.length}</span>
        </div>
      </li>
    )
  }
}

export default Card
