import React from 'react'
import PopupWithForm from './PopupWithForm'
import { CurrentUserContext } from '../contexts/CurrentUserContext'

function EditAvatarPopup(popup) {


    return (
      <PopupWithForm
      name={this.props.name}
      title={this.props.title}
      buttonText={this.props.buttonText}
      onClose={this.props.onClose}
      isOpen={this.props.isOpen}>
        <label className="popup__form-field popup__form-field_place_change-avatar">
        <input ref={this.inputRef} id="avatar-link-input" className="popup__input-field" required type="url" name="avatarLink" placeholder="Ссылка на аватар" />
        <span className="popup__input-error avatar-link-input-error"></span>
        </label>
      </PopupWithForm>
    )
}

export default EditAvatarPopup;
