class ProtectedRoute extends React.Component {
  constructor(props) {
    super(props)
  }

  render() {
    return(
      <Route>
        {this.props.loggedIn ? <Redirect exact to="./sign-up"/> : <Redirect to="./sign-in" />}
      </Route>
    )
  }
}

export default ProtectedRoute;
