class ProtectedRoute extends React.Component {
  constructor(props) {
    super(props)
  }

  render() {
    return(
      <BrowserRouter>
      <Route>
        {this.props.loggedIn ? <Redirect to="/sign-up"/> : <Redirect to="/sign-in" />}
      </Route>
      </BrowserRouter>
    )
  }
}

export default ProtectedRoute;
