import React from 'react'

class Main extends React.Component {
  constructor(props) {
    super(props);
    const addNewPlaceButton = document.querySelector('.profile__add-button')

    addNewPlaceButton.addEventListener('click', () => {})
  }


  handleEditAvatarClick = () => {
    document.querySelector('.')
  }

  handleEditProfileClick = () => {
    document.querySelector('.')
  }

  handleAddPlaceClick = () => {
    document.querySelector('.popup_type_add-place').classList.add('popup_opened')
  }


  render() {
    return (
      <main class="content">
        <section class="profile">
          <div class="profile__avatar-container">
            <div class="profile__avatar-overlay"></div>
            <img class="profile__avatar" src="<%=require('./images/profile-avatar.jpg')%>" alt="Аватар пользователя" />
          </div>
          <div class="profile__info">
            <div class="profile__info-line">
              <h1 class="profile__username"></h1>
              <button class="profile__edit-button" type="button"></button>
            </div>
            <div class="profile__info-line">
              <p class="profile__user-bio"></p>
            </div>
          </div>
          <button class="profile__add-button" type="button"></button>
        </section>

        <section class="elements">
          <ul class="elements__grid"></ul>
        </section>
      </main>
    );
  }
}

export default Main
