import React from 'react'

class Login extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      username: '',
      password: ''
    }
  }

  handleChange = event => {
    const { name, value } = event.target
    this.setState({
      [name]: value
    })
  }

  handleSubmit = event => {
    event.preventDefault()
    if (!this.state.username || !this.state.password) {
      return
    } 
  }

  render() {
    return(
      <div className="site-entrance">
        <h3 className="site-entrance__title">
          Вход
        </h3>
        <form className="site-entrance__form">
          <input
            className="site-entrance__input-field"
            required
            id="email"
            name="email"
            type="email"
            placeholder="Email" />
          <input
            className="site-entrance__input-field"
            required
            id="password"
            name="password"
            type="password"
            placeholder="Пароль" />
          <button
            className="site-entrance__submit-button"
            type="submit" >
              Войти
          </button>
        </form>
      </div>
    )
  }
}

export default Login
