import React from 'react'
import Card from './Card'
import { api } from '../utils/Api.js'
import { CurrentUserContext } from '../contexts/CurrentUserContext'

class Main extends React.Component {

  static contextType = CurrentUserContext;

  constructor(props) {
    super(props);

    this.state = {
      cards: []
    }
  }

  insertNewCard = (newCard, card) => {
    this.componentDidMount((state) => state.map((c) => c._id === card._id ? newCard : c))
  }

  handleCardDelete = card => {
    console.log(card._id)
    api.deleteCard(card.id)
  }

  handleCardLike = card => {
    const isLiked = card.likes.some(i => i._id === this.context._id);

    isLiked ?
    api.removeLike(card._id)
    .then((newCard) => {
      this.insertNewCard(newCard)
    })
    .catch(err => { 
      console.log(err)
    }) :
    api.setLike(card._id)
    .then((newCard) => {
      this.insertNewCard(newCard)
    })
    .catch(err => { 
      console.log(err)
    })
  }

  componentDidMount() {
    api.getCards()
    .then(_ => {
      this.setState({
        cards: _
      })
    })
    .catch(err => { 
      console.log(err)
    })
  }

  render() {
    return (
      <main className="content">
        <section className="profile">
          <div className="profile__avatar-container">
            <div className="profile__avatar-overlay" onClick={this.props.onEditAvatar}></div>
            <img className="profile__avatar" src={this.context.avatar} alt="Аватар пользователя" />
          </div>
          <div className="profile__info">
            <div className="profile__info-line">
              <h1 className="profile__username">
                {this.context.name}
              </h1>
              <button className="profile__edit-button" type="button" onClick={this.props.onEditProfile}></button>
            </div>
            <div className="profile__info-line">
              <p className="profile__user-bio">
                {this.context.about}
              </p>
            </div>
          </div>
          <button className="profile__add-button" type="button" onClick={this.props.onAddPlace}></button>
        </section>

        <section className="elements">
          <ul className="elements__grid">
            {this.state.cards.map(card => (
              <Card
                card={card}
                key={card._id}
                onCardClick={this.props.onCardClick}
                onCardLike={this.handleCardLike}
                onCardDelete={this.handleCardDelete}
              />
            ))}
          </ul>
        </section>
      </main>
    )
  }
}

export default Main
