import React from 'react'
import { CurrentUserContext } from '../contexts/CurrentUserContext'

class Card extends React.Component {

  static contextType = CurrentUserContext

  constructor(props) {
    super(props)

    this.state = {
      isOwn: (this.props.card.owner._id === this.context._id)
    }

    // const isOwn = this.card.owner._id === this.context._id

    // const cardDeleteButtonClassName = (
    //   `element__delete-button ${isOwn ? 'element__delete-button_visible' : ''}`
    // )

  }

  handleClick = () => {
    this.props.onCardClick(this.props.card)
    console.log(this.props.card.owner._id)
    console.log(this.context._id)
  }

  render() {
    return(
      <li className="element">
        <img className="element__image" src={this.props.card.link} alt={this.props.card.name} onClick={this.handleClick}/>
        <button className=/*{`*/'element__delete-button'/* ${this.state.isOwn ? 'element__delete-button_visible' : ''}`}*/ type="button"></button>
        <div className="element__bottom-bar">
          <h2 className="element__title">{this.props.card.name}</h2>
          <button className="element__like-button" type="button"></button>
          <span className="element__like-counter">{this.props.card.likes.length}</span>
        </div>
      </li>
    )
  }
}

export default Card
