function EditAvatarPopup(popup) {

  const inputReg = useRef()

  return (
    <PopupWithForm
    name={props.name}
    title={props.title}
    buttonText={props.buttonText}
    onClose={props.onClose}
    isOpen={props.isOpen}>
      <label className="popup__form-field popup__form-field_place_change-avatar">
      <input ref={inputRef} id="avatar-link-input" className="popup__input-field" required type="url" name="avatarLink" placeholder="Ссылка на аватар" />
      <span className="popup__input-error avatar-link-input-error"></span>
      </label>
    </PopupWithForm>
  )
}

export default EditAvatarPopup;
