import React from 'react';
import headerLogo from '../images/headerLogo.svg';
import { Link, withRouter } from 'react-router-dom'

class Header extends React.Component {
  constructor(props) {
    super(props)
  }

  showInfo = () => {
    const location = this.props.history.location.pathname
    // console.log(this.props.history.location.pathname)
    console.log(location)
  }

  switchLink = () => {
    let link = { location: '', title: '' }
    const currentLocation = this.props.history.location.pathname
    if (currentLocation === "/") {
      return link = { location: '/', title: 'Выйти' }
    } else if (currentLocation === '/sign-in') {
      return link = { location: '/sign-up', title: 'Зарегистрироваться' }
    } else if (currentLocation === '/sign-up') {
      return link = { location: '/sign-in', title: 'Войти' }
    }
  }

  render() {
    return (
      <header className="header">
        <img className="header__logo" src={headerLogo} alt="Логотип сайта" />
        {/* <Link to="/">Выйти</Link>
        <Link to="/sign-in">Войти</Link> */}
        <Link className="site-entrance__redirect-link" to={this.switchLink()}>{this.switchLink().title}</Link>
        <button onClick={this.props.onLoginClick}>Войти</button>
        <button onClick={this.props.onLogoutClick}>Выйти</button>
        <button onClick={this.showInfo}>ShowInfo</button>
        <button onClick={console.log(this.switchLink())}>SwitchLink</button>

      </header>
    );
  }
}

export default withRouter(Header)
